
import tensorflow as tf
import numpy as np


def rand01(digit):
	# Add some random noise to bits, but keep always between 0 and 1

	s = abs(np.random.normal(0.0, 0.05))

	if digit == 0:

		noise = digit + s

	else:

		noise = digit - s

	return noise


